import * as actionTypes from './actions';

const initialState = {
    books:Map(),
    authors:Map()
};

const reducer = (state=initialState,action)=>
{
    switch(action.type){
      case actionTypes.ADD_BOOK:{
        const books=state.books.add(action.data.book.id,action.data.book)
        return {
          books,
          ...state.authors
        }
      }
    }
}

export default reducer;
