import React, {Component} from 'react';
import '../App.css';
import {graphql,fetchQuery} from 'react-relay';
import environment from '../Environment';
import BookList from './BookList';
import { connect } from 'react-redux';
import * as actionTypes from '../store/actions';
import Immutable from 'immutable';

const allBooksQuery = graphql`
query BookContainerQuery
{
viewer{
  allBooks {
    edges
    {
      node
      {
        id
        title
        description
        author{name}
        comments{
          user{
            id
            fullname
          }
          comment
          commentdate
        }

      }
    }
  }
}
}
`

class BookContainer extends Component {
  componentDidMount()
  {
    console.log("trying to fetch books");
    if(this.props.books.size==0)
    {
      fetchQuery(environment,allBooksQuery)
        .then(data => {
          console.log(data);
          let books=Immutable.OrderedMap();
          data.viewer.allBooks.edges.map(({node})=>
          {
            books=books.set(node.id,node);
          })
          this.props.onFetchBooks(books);
        });
    }


  }
  render(){
    console.log("books container ",this.props.books);
    return(
      <div>
        <p className="heading">Books</p>
        {this.props.books.size>0?
            <BookList books={this.props.books} onAddBook={this.props.onAddBook}/>:
        ''}
    </div>
    )

  }
}

const mapStateToProps = (state)=>
{
  return {
    books:state.books,
    authors:state.authors
  }
}

const mapDispatchToProps = (dispatch)=>
{
  return {
    onAddBook: (book)=>dispatch({type:actionTypes.ADD_BOOK,book:book}),
    onFetchBooks: (books)=>dispatch({type:actionTypes.FETCH_BOOKS,books:books})
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(BookContainer);
