import React, {Component} from 'react';
import '../App.css';
import {graphql,fetchQuery} from 'react-relay';
import environment from '../Environment';
import BookList from './BookList';
import { connect } from 'react-redux';
import * as actionTypes from '../store/actions';

const allBooksQuery = graphql`
query booksQuery
{
viewer{
  allBooks {
    edges
    {
      node
      {
        Book_book
      }
    }
  }
}
}
`
class BookContainer extends Component {
  componentDidMount()
  {
    fetchQuery(environment,allBooksQuery)
      .then(data => {
        const books=new Map();
        data.viewer.allBooks.edges.map(({node})=>
        {
          books.set(node.id,node);
        })
        this.props.onFetchBooks(books);
      });

  }
  render(){

    return(
      this.props.books!==null && this.props.books!=undefined ? <BookList books={this.props.books} onAddBook={this.props.onAddBook}/>:''
    )

  }
}

const mapStateToProps = (state)=>
{
  return {
    books:state.books
  }
}

const mapDispatchToProps = (dispatch)=>
{
  return {
    onAddBook: (book)=>dispatch({type:actionTypes.ADD_BOOK,book:book}),
    onFetchBooks: (books)=>dispatch({type:actionTypes.FETCH_BOOKS,books:books})
  }
}


export default connect(mapStateToProps,mapDispatchToProps)(BookContainer);
