import * as actionTypes from './actions';
import Immutable from 'immutable';

const initialState = {
    books: Immutable.Map(),
    authors:Immutable.Map()
};

const reducer = (state=initialState,action)=>
{
    switch(action.type){
      case actionTypes.ADD_BOOK:{
      const books=state.books.add(action.book.id,action.book);

      console.log("from reducer",books);
        return {
          ...state,
          books:books
          }
      }
      case actionTypes.FETCH_BOOKS:{
        return {
          ...state,
          books:action.books,
        }
      }
      case actionTypes.ADD_AUTHOR:{
        const authors=state.authors.set(action.authorid,action.author);
        return {
          ...state,
          authors:authors
        }
      }
      case actionTypes.FETCH_AUTHORS:{
        return {
          ...state,
          authors:action.authors
        }
      }
      default : return state;
    }
}

export default reducer;
